import React, { useState } from 'react';
import axios from 'axios';

const AddVideos = () => {
  const [videoTitle, setVideoTitle] = useState('');
  const [videoDescription, setVideoDescription] = useState('');
  const [videoFile, setVideoFile] = useState(null);
  const [error, setError] = useState('');

  const handleFileChange = (event) => {
    setVideoFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');

    const formData = new FormData();
    formData.append('title', videoTitle);
    formData.append('description', videoDescription);
    formData.append('video_file', videoFile);

    const token = localStorage.getItem('token');

    if (!token) {
      console.error('No token found, please log in');
      setError('Please log in to upload videos.');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/videos/create/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`,
        },
      });
      console.log('Video created successfully', response.data);
    } catch (error) {
      console.error('Error creating video', error.response?.data || error.message);
      setError(error.response?.data?.detail || 'Error creating video');
    }
  };

  return (
    <div>
      <form className="max-w-sm mx-auto w-96 ml-44 my-44" onSubmit={handleSubmit}>
        <div className="mb-5">
          <input
            type="text"
            id="videotitle"
            className="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
            placeholder="Enter video Title"
            value={videoTitle}
            onChange={(e) => setVideoTitle(e.target.value)}
            required
          />
        </div>
        <div className="mb-5">
          <input
            type="text"
            id="videodescription"
            className="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
            placeholder="Enter video Description"
            value={videoDescription}
            onChange={(e) => setVideoDescription(e.target.value)}
            required
          />
        </div>
        <div className="mb-5">
          <input
            type="file"
            id="uploadfile"
            className="shadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
            onChange={handleFileChange}
            required
          />
        </div>
        {error && <p className="text-red-500">{error}</p>}
        <button
          type="submit"
          className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center"
        >
          Save video
        </button>
      </form>
    </div>
  );
};

export default AddVideos;
