import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Videos = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [videoList, setVideoList] = useState([]);
  const [editingVideo, setEditingVideo] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [videoFile, setVideoFile] = useState(null);


  // Fetch video list from API on component mount
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/videos/');
        setVideoList(response.data);
      } catch (error) {
        console.error('Failed to fetch videos', error);
      }
    };

    fetchVideos();
  }, []);

  // Helper to get token
  const getToken = () => {
    const token = localStorage.getItem('token');
    console.log('Access token:', token);  // Debugging token issues
    if (!token) {
      alert("No access token found, please log in");
      return null;
    }
    return token;
  };

  // Delete video
  const deleteVideo = async (id) => {
    const token = getToken();
    if (!token) {
      alert('Please log in to delete videos.');
      return;
    }

    if (window.confirm('Are you sure you want to delete this video?')) {
      try {
        await axios.delete(`http://localhost:8000/api/videos/${id}/delete/`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        setVideoList(videoList.filter(video => video.id !== id));
      } catch (error) {
        if (error.response && error.response.status === 401) {
          alert('Unauthorized: Token may be expired or invalid. Please log in again.');
          localStorage.removeItem('access_token'); // Optionally clear the token
        } else {
          console.error('Failed to delete video', error);
        }
      }
    }
  };

  // Update video
  const updateVideo = async () => {
    const token = getToken();
    if (!token) {
      alert('Please log in to update videos.');
      return;
    }

    if (editingVideo) {
      try {
        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        if (videoFile) formData.append('video_file', videoFile);

        const response = await axios.put(`http://localhost:8000/api/videos/${editingVideo.id}/update/`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
            'Authorization': `Bearer ${token}`,
          },
        });

        setVideoList(videoList.map(video => video.id === editingVideo.id ? response.data : video));
        cancelEditing();
      } catch (error) {
        if (error.response && error.response.status === 401) {
          alert('Unauthorized: Token may be expired or invalid. Please log in again.');
          localStorage.removeItem('access_token'); // Optionally clear the token
        } else {
          console.error('Failed to update video', error);
        }
      }
    }
  };

  // Start editing a video
  const startEditing = (video) => {
    setEditingVideo(video);
    setTitle(video.title);
    setDescription(video.description);
    setVideoFile(null);
  };

  // Cancel editing
  const cancelEditing = () => {
    setEditingVideo(null);
    setTitle('');
    setDescription('');
    setVideoFile(null);
  };

  // Handle file change for video
  const handleFileChange = (e) => {
    setVideoFile(e.target.files[0]);
  };

  // Filter videos based on search query
  const filteredVideos = videoList.filter(video =>
    video.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    video.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="container mx-auto my-12 px-4 sm:px-6 lg:px-8">
      <h1 className="text-center text-3xl font-bold mb-8">Videos</h1>

      <div className="mb-8 flex justify-center">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search videos"
          className="p-2 border border-gray-300 rounded-lg w-full max-w-md"
        />
      </div>

      {editingVideo && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4">Edit Video</h2>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            className="p-2 border border-gray-300 rounded-lg w-full mb-2"
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            className="p-2 border border-gray-300 rounded-lg w-full mb-4"
          />
          <input
            type="file"
            onChange={handleFileChange}
            className="mb-4"
          />
          <button onClick={updateVideo} className="bg-blue-500 text-white p-2 rounded-lg mr-2">
            Update
          </button>
          <button onClick={cancelEditing} className="bg-gray-500 text-white p-2 rounded-lg">
            Cancel
          </button>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-4">
        {filteredVideos.map(video => (
          video && (
            <div key={video.id} className="video-item">
              <h2 className="text-xl font-semibold mb-2">{video.title}</h2>
              <p className="mb-4">{video.description}</p>
              <video width="320" height="240" controls>
                <source src={video.video_file} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
              <div className="mt-4">
                <button onClick={() => startEditing(video)} className="bg-yellow-500 text-white p-2 rounded-lg mr-2">
                  Edit
                </button>
                <button onClick={() => deleteVideo(video.id)} className="bg-red-500 text-white p-2 rounded-lg">
                  Delete
                </button>
              </div>
            </div>
          )
        ))}
      </div>
    </div>
  );
};

export default Videos;
